SELECT left(CAT.C_REF,1)AS CODE_METIER,CAT.L_REF,L.L_NIVEAU,E.L_EQUIPE,L.*, C.* FROM IWSPROD.suiteisilog.LIGNECTR L INNER JOIN IWSPROD.suiteisilog.CONTRAT C ON L.NO_CTR=C.NO_CTR INNER JOIN IWSPROD.suiteisilog.OBJET o on o.C_OBJET=L.C_OBJSERVICE INNER JOIN IWSPROD.suiteisilog.CATALOGUE CAT ON CAT.C_REF=o.C_REF INNER JOIN IWSPROD.suiteisilog.EQUIPE E ON E.C_EQUIPE=C.C_EQUIPECONTRAT   WHERE L.D_ARCHIVE IS NULL AND C.C_TYPECTR IN ('OLA') order by   CODE_METIER,CAT.L_REF,L.L_NIVEAU

